    // Asigna id_empresa a cada producción
    this.producciones.forEach(p => p.id_empresa = this.idEmpresa); // Enviar cada producción por separado

    this.producciones.forEach(produccion => {
      this.oneService.enviarDatosProduccion(produccion).subscribe(response => {
        console.log('Producción enviada correctamente', response);
      }, error => {
        console.error('Error al enviar la producción', error);
      });
    }); // Si todo va bien, pasar al siguiente step

    if (this.currentStep < 10) {
      this.currentStep++;
      this.updateStepVisibility();
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    }
  }

