import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
// import { bienes, datos_empresas, domicilio_industrial, email_respondente, nombres_fantasia, opciones_servicios, produccion, productos, respondente, servicios, telefonos_empresa, telefonos_repondente, unidad_medidas } from 'src/app/Interfaces/models';
import {  produccion, unidad_medidas, servicios_basicos, remuneraciones_cargas, DatosEmpresa, DatosRespondiente, UtilizacionInsumos, UtilizacionServicio, InsumosBasicos, manoDeObra  } from 'src/app/Interfaces/models';
import { ServicesService } from 'src/app/services/services.service';
import { HttpClient } from '@angular/common/http';
import { Subject } from 'rxjs';
import { debounceTime, switchMap } from 'rxjs/operators';

@Component({
  selector: 'app-one',
  templateUrl: './one.component.html',
  styleUrls: ['./one.component.css']
})
export class OneComponent implements OnInit {
  currentStep = 1;
  unidad_medida = unidad_medidas;
  // empresas: datos_empresas[] = [];
  // selectedEmpresa: datos_empresas | null = null;
  
  private searchTermSubject = new Subject<string>();
  
  servicios: { id: number;  nombre: string; monto_pesos: number; }[] = [];
  
  producciones: produccion[] = [];
  produccion: any[] = [];  // Aquí tienes los datos de producción, asumo que ya está inicializado

  bieness: UtilizacionInsumos[] = [];
  servicioss: UtilizacionServicio[] = [];
  servicio_basic: InsumosBasicos [] = [];
  servicio_basico = servicios_basicos;
  remuneraciones_cargas : manoDeObra[] = [];
  remuneracion_carga = remuneraciones_cargas;

  // step3
  utilacion_Servicio: UtilizacionServicio[] = [];
  insumos_Basicos: InsumosBasicos [] = [];
  mano_Obra :manoDeObra [] = [];
//Recibir id_empresa de tabla encuestas
idEmpresa: number = 0 ;



  datosEmpresa: DatosEmpresa = {
    id : 0,
    nombreEmpresa: '',
    nombreFantasia: '',
    cuit: '',
    direccionEstablecimiento: '',
    direccionAdministracion: '',
    localidadEstablecimiento: '',
    actividadPrincipal: '',

  };

  datosRespondiente: DatosRespondiente = {
    id: 0,
    nombreApellido: '',
    cargoArea: '',
    tipoTelefono: 'Particular',
    numeroTelefono: 0,
    email: ''
  };
 
  constructor(
    private router: Router,
    private route: ActivatedRoute,
    private oneService: ServicesService,
    private http: HttpClient,
  ) { }


  
  ngOnInit(): void {
    this.agregarNuevaFila(); // Agregar una fila inicial
    this.agregarNuevaFila3(); // Agregar una fila inicial
    this.agregarNuevaFilaServicio();
    this. agregarNuevaFilaServicioBasico()
    this.idEmpresa = Number(this.route.snapshot.paramMap.get('id'));
    console.log('ID Empresa recibido:', this.idEmpresa);
  }

  onSearchChange(term: string) {
    this.searchTermSubject.next(term);
  }


 
  actualizarProduccion(index: number, nombre: string) {
    this.producciones[index].producto = nombre;
  }



  // AGREGAR FILAS

  agregarNuevaFila() {
    const nuevaProduccion: produccion = {
      id: 0,
      producto: '',
      unidad_medida: null,
      mercado_interno: 0,
      mercado_externo: 0
    };
    this.producciones.push(nuevaProduccion);
  }

  

agregarNuevaFila3() {
  const nuevaBienes: UtilizacionInsumos = {
    id: 0,
    producto: '',
    unidad_medida: null,
    cantidad: 0,
    monto_pesos: 0,
  };
  this.bieness.push(nuevaBienes);
}

  agregarNuevaFilaServicio() {
    this.servicios.push({ id: 0,  nombre: '', monto_pesos: 0 });
  }

  agregarNuevaFilaServicioBasico(){
    const nuevaServicioBasico: InsumosBasicos = {
      id: 0,
      tipo: '4.9. Energía eléctrica consumida (kw/h)',
      cantidad: 0,
      monto_pesos: 0,
    };
    this.servicio_basic.push(nuevaServicioBasico);
  }

  agregarNuevaFilaRemuneracionCarga(){
    const nuevaRemuneracionCarga: manoDeObra = {
      id: 0,
      tipo: '4.13. Sueldos y Jornales Brutas totales, incluido SAC y horas extras',
      monto_pesos: 0,
    };
    this.remuneraciones_cargas.push(nuevaRemuneracionCarga);
  }


  //ELIMINAR FILAS

  eliminarUltimaFilaProduccion() {
    if (this.producciones.length > 0) {
      this.producciones.pop();
    }
  }
  
  eliminarUltimaFilaServicios() {
    if (this.servicios.length > 0)  {
      this.servicios.pop();

    }
  }
  
  eliminarUltimaFilaBienes() {
    if (this.bieness.length > 0) {
      this.bieness.pop();
    }
  }


  eliminarUltimaFilaServicioBasico(){
    if (this.servicio_basic.length > 0) {
      this.servicio_basic.pop();
    }
  }
  

  eliminarUltimaFilaRemuneracionCarga(){
    if (this.remuneraciones_cargas.length > 0) {
      this.remuneraciones_cargas.pop();
    }
  }
  

        // this.router.navigate(['/one', idEmpresa]);


    EnviarS1() {      
      console.log(this.datosEmpresa); // Verificar datos enviados
    
      // Agrega el idEmpresa a los datos que vas a enviar
      this.datosEmpresa.id_empresa = this.idEmpresa;
      this.datosRespondiente.id_empresa = this.idEmpresa;
    
      // Enviar datos de la empresa
      this.oneService.enviarDatosEmpresa(this.datosEmpresa).subscribe({
        next: (response) => {
          console.log('Datos enviados exitosamente:', response);
          if (this.currentStep < 10) {
            this.currentStep++;
            this.updateStepVisibility();
            window.scrollTo({ top: 0, behavior: 'smooth' });
          }    
        },
        error: (error) => {
          console.error('Error al enviar los datos:', error);
        },
        complete: () => {
          console.log('Solicitud completada.');
        }
      });
    
      // Enviar datos del respondiente
      this.oneService.enviarDatosRespondiente(this.datosRespondiente).subscribe(
        (response) => {
          console.log('Datos del respondiente enviados exitosamente:', response);
        },
        (error) => {
          console.error('Error al enviar los datos del respondiente:', error);
        }
      );
    }
 

    step2() {
      // Asigna id_empresa a cada producción
      this.producciones.forEach(p => p.id_empresa = this.idEmpresa);
    
      // Enviar cada producción por separado
      this.producciones.forEach((produccion) => {
        this.oneService.enviarDatosProduccion(produccion).subscribe(
          response => {
            console.log('Producción enviada correctamente', response);
          },
          error => {
            console.error('Error al enviar la producción', error);
          }
        );
      });
    
      // Si todo va bien, pasar al siguiente step
      if (this.currentStep < 10) {
        this.currentStep++;
        this.updateStepVisibility();
        window.scrollTo({ top: 0, behavior: 'smooth' });
      }
    }


   async step3() {
      // Asigna id_empresa a cada fila de las tablas antes de enviar
      this.bieness.forEach(insumo => insumo.id_empresa = this.idEmpresa);
      this.utilacion_Servicio.forEach(servicio => servicio.id_empresa = this.idEmpresa);
      this.insumos_Basicos.forEach(insumoBasico => insumoBasico.id_empresa = this.idEmpresa);
      this.mano_Obra.forEach(manoObra => manoObra.id_empresa = this.idEmpresa);
    
      // Enviar utilacion_Insumo (Insumos)
      this.bieness.forEach((insumo) => {
        this.oneService.enviarDatosBienes(insumo).subscribe(
          response => {
            console.log('Insumos enviados correctamente', response);
            console.log(insumo);  // Para bienes

          },
          error => {
            console.error('Error al enviar los insumos', error);
          }
        );
      });
    
      // Enviar utilacion_Servicio (Servicios)
      this.servicioss.forEach((servicio) => {
        this.oneService.enviarDatosServicios(servicio).subscribe(
          response => {
            console.log('Servicios enviados correctamente', response);
            console.log(servicio);  // Para servicios

          },
          error => {
            console.error('Error al enviar los servicios', error);
          }
        );
      });
    
      // Enviar insumos_Basicos (Servicios Básicos)
      this.servicio_basic.forEach((insumoBasico) => {
        this.oneService.enviarDatosServiciosBasicos(insumoBasico).subscribe(
          response => {
            console.log('Insumos básicos enviados correctamente', response);
          },
          error => {
            console.log(insumoBasico);  // Para insumos básicos
            console.error('Error al enviar los insumos básicos', error);
          }
        );
      });
    
      // Enviar mano_Obra (Remuneraciones y cargas)
      if (this.remuneraciones_cargas && this.remuneraciones_cargas.length > 0) {
        this.remuneraciones_cargas.forEach((manoObra) => {
          if (!manoObra) {
            this.oneService.enviarManoDeObra(manoObra).subscribe(
              response => {
                console.log('Remuneraciones y cargas enviadas correctamente', response);
                console.log("mano", manoObra);  // Para mano de obra
              },
              error => {
                console.error('Error al enviar las remuneraciones y cargas', error);
              }
            );
          } else {
            console.error('manoObra es undefined');
          }
        });
      } else {
        console.error('La lista de remuneraciones_cargas está vacía o es undefined');
      }
      
    
      // Si todo va bien, pasar al siguiente paso
      if (this.currentStep < 10) {
        this.currentStep++;
        this.updateStepVisibility();
        window.scrollTo({ top: 0, behavior: 'smooth' });
      }

    }

  nextStep() {
    if (this.currentStep < 10) {
      this.currentStep++;
      this.updateStepVisibility();
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  }

  

  previousStep() {
    if (this.currentStep > 1) {
      this.currentStep--;
      this.updateStepVisibility();
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  }

  // nextStepper() {
  //   this.router.navigate(['/two']);
  //   window.scrollTo({ top: 0, behavior: 'smooth' });
  // }

  updateStepVisibility() {
    for (let i = 1; i <= 10; i++) {
      const step = document.getElementById(`step-${i}`);
      if (step) {
        if (i === this.currentStep) {
          step.classList.remove('hidden');
        } else {
          step.classList.add('hidden');
        }
      }
    }
  }
}
<app-header></app-header>
<br>

<div class="container mx-auto p-4">
  <div class="mb-8">
    <ol class="flex items-center justify-between w-full">
      <li class="flex items-center w-full"
        [ngClass]="{
          'text-[#FF8300] dark:text-[#FF8300]': currentStep >= 1
        }">
        <span class="flex items-center justify-center w-10 h-10 rounded-full lg:h-12 lg:w-12 shrink-0"
          [ngClass]="{
            'bg-[#FF8300] dark:bg-[#FF8300]': currentStep >= 1,
            'bg-gray-100 dark:bg-gray-700': currentStep < 1
          }">
          <svg class="w-3.5 h-3.5 text-white lg:w-4 lg:h-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 12">
            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5.917 5.724 10.5 15 1.5"/>
          </svg>
        </span>
        <div class="flex-grow h-1 mx-2"
          [ngClass]="{
            'bg-[#FF8300]': currentStep >= 2,
            'bg-gray-300 dark:bg-gray-700': currentStep < 2
          }"></div>
      </li>

      <li class="flex items-center w-full"
        [ngClass]="{
          'text-[#FF8300] dark:text-[#FF8300]': currentStep >= 2
        }">
        <span class="flex items-center justify-center w-10 h-10 rounded-full lg:h-12 lg:w-12 shrink-0"
          [ngClass]="{
            'bg-[#FF8300]': currentStep >= 2,
            'bg-gray-100 dark:bg-gray-700': currentStep < 2
          }">
          <span class="text-white">2</span>
        </span>
        <div class="flex-grow h-1 mx-2"
          [ngClass]="{
            'bg-[#FF8300]': currentStep >= 3,
            'bg-gray-300 dark:bg-gray-700': currentStep < 3
          }"></div>
      </li>

      <li class="flex items-center">
        <span class="flex items-center justify-center w-10 h-10 rounded-full lg:h-12 lg:w-12 shrink-0"
          [ngClass]="{
            'bg-[#FF8300]': currentStep >= 3,
            'bg-gray-100 dark:bg-gray-700': currentStep < 3
          }">
          <span class="text-white">3</span>
        </span>
      </li>
    </ol>
  </div>
</div>

  
  <div id="step-1">
    <form >
        
      <div class="container bg-white p-6 shadow-md rounded-lg mx-auto p-4 " >
        <h1 class="text-2xl font-bold mb-4 text-center">RELEVAMIENTO INDUSTRIAL MANUFACTURERO DE SAN JUAN</h1>

       
        <p class="mb-4 text-sm italic">CARÁCTER ESTRICTAMENTE CONFIDENCIAL Y RESERVADO - Ley N° 17.622</p>
       
        <div class="bg-gray-100 p-4 mb-4 text-sm" >
          <p>Las informaciones que se suministren a los organismos que integran el Sistema Estadístico Nacional, en cumplimiento de la presente ley, serán estrictamente secretas y sólo se utilizarán con fines estadísticos. Los datos deberán ser suministrados y publicados, exclusivamente, en compilaciones de conjunto, de modo que no pueda ser violado el secreto comercial o patrimonial, ni individualizarse las personas o entidades a quienes se refieran. Quedan exceptuados del secreto estadístico los siguientes datos de registro: nombre y apellido, o razón social, domicilio y rama de actividad.</p>
        </div>
      
        <form >
          <h2 class="text-xl font-semibold mb-2 text-center">DATOS DE IDENTIFICACIÓN</h2>
         
          <div class="mb-4">
            <label class="block mb-1">1. Nombre o razón social de la empresa:</label>
            <input type="text" class="w-full border p-2" name="nombreEmpresa" [(ngModel)]="datosEmpresa.nombreEmpresa">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">1.1.1 Nombre de Fantasía:</label>
            <input type="text" class="w-full border p-2" name="nombreFantasia" [(ngModel)]="datosEmpresa.nombreFantasia">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">1.1.2 CUIT:</label>
            <input type="text" class="w-full border p-2" name="cuit" [(ngModel)]="datosEmpresa.cuit">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">1.2.1 Dirección del Establecimiento Industrial:</label>
            <input type="text" class="w-full border p-2" name="direccionEstablecimiento" [(ngModel)]="datosEmpresa.direccionEstablecimiento">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">1.2.2 Dirección de la Administración:</label>
            <input type="text" class="w-full border p-2" name="direccionAdministracion" [(ngModel)]="datosEmpresa.direccionAdministracion">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">1.2.3 Partido o Localidad Est. Industrial:</label>
            <input type="text" class="w-full border p-2" name="localidadEstablecimiento" [(ngModel)]="datosEmpresa.localidadEstablecimiento">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">1.2.4 Actividad principal:</label>
            <input type="text" class="w-full border p-2" name="actividadPrincipal" [(ngModel)]="datosEmpresa.actividadPrincipal">
          </div>
          
          <h2 class="text-xl font-semibold mb-2 text-center">DATOS DEL RESPONDIENTE DE LA ENCUESTA</h2>

          <div class="mb-4">
            <label class="block mb-1">2.1. Nombre y Apellido:</label>
            <input type="text" class="w-full border p-2" name="nombreApellido" [(ngModel)]="datosRespondiente.nombreApellido">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">2.2. Cargo/Área:</label>
            <input type="text" class="w-full border p-2" name="cargoArea" [(ngModel)]="datosRespondiente.cargoArea">
          </div>
          
          <div class="mb-4">
            <label class="block mb-1">2.3. Tipo de Teléfono (Particular/Corporativo):</label>
            <select class="w-full border p-2" name="tipoTelefono" [(ngModel)]="datosRespondiente.tipoTelefono">
              <option value="Particular">Particular</option>
              <option value="Corporativo">Corporativo</option>
            </select>
          </div>
          <div class="mb-4">
            <label class="block mb-1">2.4. Numero telefonico:</label>
            <input type="number" class="w-full border p-2" name="numeroTelefono" [(ngModel)]="datosRespondiente.numeroTelefono">
          </div>
          <div class="mb-4">
            <label class="block mb-1">2.5. Email:</label>
            <input type="email" class="w-full border p-2" name="email" [(ngModel)]="datosRespondiente.email">
          </div>
          
        </form>
        <br>
        <div class="flex justify-center">
          <button type="button" class="bg-[#FF8300] text-white px-4 py-2 rounded" (click)="EnviarS1()">Siguiente</button>
        </div>
      </div>
    
          </form>
  </div>




  <div id="step-2" class="hidden">
    <div class="container bg-white p-6 shadow-md rounded-lg mx-auto p-4 text-center">
      <h1 class="text-2xl font-bold mb-4">INDUSTRIA MANUFACTURERA - PRODUCCIÓN Y VENTAS EN UNIDADES FÍSICAS</h1>
      
      <div class="mb-4">
        <label class="block text-sm font-medium text-gray-700">Periodo a declarar:</label>
        <input type="text" value="Acumulado 2023" class="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50" readonly>
      </div>
    
      <table class="w-full border-collapse border border-gray-300">
        <thead>
          <tr class="bg-gray-100">
            <th class="border border-gray-300 px-4 py-2">a -Producto</th>
            <th class="border border-gray-300 px-4 py-2">b- Unidad de medida del producto</th>
            <th class="border border-gray-300 px-4 py-2">c- Mercado Interno (en unidades físicas)</th>
            <th class="border border-gray-300 px-4 py-2">d- Mercado Externo (en unidades físicas)</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let produccion of producciones; let i = index">
            <td class="border border-gray-300 px-4 py-2">
              <input type="text" [(ngModel)]="produccion.producto" class="w-full">
              <!-- <input type="text" [(ngModel)]="produccion.producto" (input)="onBuscarOAgregarProducto($event, i)" class="w-full"> -->
            </td>
            <td class="border border-gray-300 px-4 py-2">
              <select [(ngModel)]="produccion.unidad_medida">
                <option *ngFor="let opcion of unidad_medida" [value]="opcion">{{ opcion }}</option>
              </select>
            </td>
            <td class="border border-gray-300 px-4 py-2">
              <input type="number" [(ngModel)]="produccion.mercado_interno" class="w-full">
            </td>
            <td class="border border-gray-300 px-4 py-2">
              <input type="number" [(ngModel)]="produccion.mercado_externo" class="w-full">
            </td>
          </tr>
        </tbody>
      </table>
      <br>
      <button (click)="agregarNuevaFila()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">
        Agregar nueva fila
      </button>
      <button (click)="eliminarUltimaFilaProduccion()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">Eliminar última fila</button>

      <div class="mt-4">
        <textarea class="w-full border border-gray-300 p-2" rows="3" placeholder="Si en alguna pregunta tuvo problemas para completar la respuesta o si tiene alguna observación que hacer, por favor utilice este espacio:"></textarea>
      </div>
      <br>
      <div class="flex justify-center">
        <button type="button" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2" (click)="previousStep()">Anterior</button>
        <button type="button" class="bg-[#FF8300] text-white px-4 py-2 rounded" (click)="step2()">Siguiente</button>
      </div>
    </div>
      </div>


















      

  <div id="step-3" class="hidden">
    <div class="container mx-auto p-4 bg-white p-6 shadow-md rounded-lg text-center">
      <h1 class="text-2xl font-bold mb-4">INDUSTRIA MANUFACTURERA - UTILIZACIÓN DE INSUMOS</h1>
      
      <div class="mb-4">
        <span class="font-semibold">Periodo a declarar:</span>
        <span class="ml-2">Acumulado 2023</span>
      </div>
    
      <div class="bg-gray-200 p-2 mb-4">
        <h2 class="font-bold">UTILIZACIÓN DE INSUMOS</h2>
        <p class="text-sm mt-2">a-Insumo: Se requiere enumerar de 1 a 4 insumos de mayor relevancia en los costos de la entidad, durante el último año. b-Unidad de medida: Se refiere a la magnitud en que se mide la producción (Toneladas, Kgs, metros, metros cúbicos, litros, pares, docenas, quintales, centímetros, hectolitros, packs, unidades físicas, otras unidades, etc.). c-Cantidad utilizada: Magnitud del insumo utilizado, expresado en la unidad de medida enunciada en la columna b-. d-Monto Total en pesos (sin centavos): Monto Total devengado destinado a la compra de insumos (precio unitario x cantidad utilizada). En todos los casos los montos en pesos ($) deben expresarse sin incluir impuestos. Si los insumos fueran suministrados por otros establecimientos de la misma empresa, para continuar o concluir el proceso productivo, deben valorizar los mismos al precio estimable para esa transferencia.</p>
      </div>
    
      <table class="w-full border-collapse border border-gray-300 mb-6">
        <thead>
          <tr class="bg-gray-100">
            <th class="border border-gray-300 px-4 py-2">a- Nombre Insumos</th>
            <th class="border border-gray-300 px-4 py-2">b- Unidades de medida del insumo</th>
            <th class="border border-gray-300 px-4 py-2">c- Cantidad utilizada</th>
            <th class="border border-gray-300 px-4 py-2">d- Monto Total en pesos (sin centavos)</th>
          </tr>
        </thead>
        <tbody>
          <ng-container >
              <tr *ngFor="let bienes of bieness; let i = index">

              <td class="border border-gray-300 px-4 py-2">
                <input type="text" class="w-full" >
              </td>
              <td class="border border-gray-300 px-4 py-2">
                <select [(ngModel)]="bienes.unidad_medida">
                  <option *ngFor="let opcion of unidad_medida" [value]="opcion">{{ opcion }}</option>
                </select>
              </td>
              <td class="border border-gray-300 px-4 py-2">
                <input [(ngModel)]="bienes.cantidad" type="number" class="w-full" >
              </td>
              <td class="border border-gray-300 px-4 py-2">
                <input  [(ngModel)]="bienes.monto_pesos" type="number" class="w-full" >
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
      <button (click)="agregarNuevaFila3()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">
        Agregar nueva fila
      </button> 
      <button (click)="eliminarUltimaFilaBienes()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">Eliminar última fila </button>

      <br>  
          <br>  
    
      <div class="bg-gray-200 p-2 mb-4">
        <h2 class="font-bold">UTILIZACIÓN DE SERVICIOS</h2>
        <p class="text-sm mt-2">a- Principales Servicios: Se requiere enumerar de 1 a 4 insumos de mayor relevancia en los principales servicios demandados, durante el último año. Son ejemplo de ello: Fletes, seguros, telecomunicaciones, alquileres, servicios profesionales, etc. b-Monto total en pesos (sin centavos): Monto total devengado destinado al pago de servicios. En todos los casos los montos en pesos ($) deben expresarse sin incluir impuestos.</p>
      </div>
    
      <table class="w-full border-collapse border border-gray-300 mt-4">
        <thead>
          <tr class="bg-gray-100">
            <th class="border border-gray-300 px-4 py-2">Nombre del Servicio</th>
            <th class="border border-gray-300 px-4 py-2">Monto Total en pesos (sin centavos)</th>
          </tr>
        </thead>
        <tbody>
          <ng-container *ngFor="let servicio of servicios; let i = index">
            <tr>
              <td class="border border-gray-300 px-4 py-2">
                <input [(ngModel)]="servicio.nombre" type="text" class="w-full" [attr.id]="'servicio-' + i + '-nombre'">
              </td>
              <td class="border border-gray-300 px-4 py-2">
                <input [(ngModel)]="servicio.monto_pesos" type="number" class="w-full" [attr.id]="'servicio-' + i + '-monto'">
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
        <br>
      <button (click)="agregarNuevaFilaServicio()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">
        Agregar nueva fila
      </button> 
      <button (click)="eliminarUltimaFilaServicios()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">Eliminar última fila</button>
    </div>
      <br>
    <div class="container mx-auto p-4 bg-white p-6 shadow-md rounded-lg">
      <div class="bg-gray-200 p-4 mb-6">
        <h2 class="text-xl font-bold mb-2">CONSUMO DE PRINCIPALES INSUMOS BÁSICOS</h2>
        <p class="text-sm">
          Energía eléctrica: Utilizada durante el año de referencia en kw/h (excluir energía reactiva). Corresponde al total de kw/h y pesos ($) de energía comprada mas la autoproducida, menos la vendida a terceros. Gas Oil: Expendido durante el año de referencia en litros. Corresponde al total de litros y pesos ($) gastados en combustibles. En ambos casos los montos en pesos ($) deben expresarse sin incluir impuestos. Gas consumido: Consumido en el año de referencia en m3. Agua: Consumida durante el año de referencia, medida en litros u m3. Si carece de medidor, estimar el consumo.
        </p>
      </div>
    
      <table class="w-full border-collapse border border-gray-300 mb-6">
        <thead>
          <tr class="bg-gray-100">
            <th class="border border-gray-300 px-4 py-2"></th>
            <th class="border border-gray-300 px-4 py-2">a- Cantidad utilizada (en kw/h /lts/ m3)</th>
            <th class="border border-gray-300 px-4 py-2">b- Monto Total en pesos (sin centavos)</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let servicioB of servicio_basic; let i = index">
            <td class="border border-gray-300 px-4 py-2">
              <select [(ngModel)]="servicioB.tipo">
                <option *ngFor="let opcion of servicio_basico" [value]="opcion">{{ opcion }}</option>
              </select>
            </td>
            <td class="border border-gray-300 px-4 py-2">
              <input [(ngModel)]="servicioB.cantidad" type="number" class="w-full" >
            </td>
            <td class="border border-gray-300 px-4 py-2">
              <input [(ngModel)]="servicioB.monto_pesos" type="number" class="w-full" >
            </td>
          </tr>
        </tbody>
      </table>
      <div class="flex justify-center">
        <button (click)="agregarNuevaFilaServicioBasico()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">
          Agregar nueva fila
        </button> 
        <button (click)="eliminarUltimaFilaServicioBasico()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">Eliminar última fila </button>
      </div>
      <br>

      <div class="bg-gray-200 p-4 mb-6">
        <h2 class="text-xl font-bold mb-2">VALORIZACIÓN DE MANO DE OBRA</h2>
        <p class="text-sm">
          Deben declararse retribuciones devengadas al personal asalariado de la empresa, esté vinculado o no con el proceso productivo, durante el año de referencia. Debe incluirse la remuneración bruta por todo concepto, incluyendo vacaciones gozadas, SAC, Horas extras, y cualquier otro pago habitual al personal. No deben ser incluidos los pagos por asignaciones familiares, preaviso, indemnizaciones, despidos, gratificaciones por cese de tareas, ni retiros voluntarias, tampoco vacaciones no gozadas.
        </p>
      </div>
    
      <table class="w-full border-collapse border border-gray-300">
        <thead>
          <tr class="bg-gray-100">
            <th class="border border-gray-300 px-4 py-2"></th>
            <th class="border border-gray-300 px-4 py-2">a - Monto Total en pesos (sin centavos)</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let remuCarga of remuneraciones_cargas; let i = index">
            <select [(ngModel)]="remuCarga.tipo">
              <option *ngFor="let opcion of remuneracion_carga" [value]="opcion">{{ opcion }}</option>
            </select>
          <td class="border border-gray-300 px-4 py-2">
            <input [(ngModel)]="remuCarga.monto_pesos" type="number" class="w-full" >
          </td>
          </tr>

        </tbody>
      </table>
      <div class="flex justify-center">
        <button (click)="agregarNuevaFilaRemuneracionCarga()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">
          Agregar nueva fila
        </button> 
        <button (click)="eliminarUltimaFilaRemuneracionCarga()" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2">Eliminar última fila </button>
      </div>
      <div class="mt-4">
        <textarea class="w-full border border-gray-300 p-2" rows="3" placeholder="Si en alguna pregunta tuvo problemas para completar la respuesta o si tiene alguna observación que hacer, por favor utilice este espacio:"></textarea>
      </div>
      <div class="flex justify-center">
        <button type="button" class="bg-[#FF8300] text-white px-4 py-2 rounded mr-2" (click)="previousStep()">Anterior</button>
        <button type="button" class="bg-[#FF8300] text-white px-4 py-2 rounded" (click)="step3()">Siguiente</button>
      </div>
  </div>
      </div>
